import * as api from "../api";

export const getStories=()=>async(dispatch)=>{
    try{
        const {data}=await api.fetchStories();
        const action={type:"FETCH_ALL_STORIES",payload:data};
        dispatch(action);
    }
    catch(error){
        console.log(error.message);
    }
    
};
