import axios from "axios";
const api=axios.create({baseURL:"http://localhost:5001"});

export const fetchStories=async()=>axios.get("/stories");
export const createStory=async(story)=>axios.post("/stories",story);
export const updateStory=async(id,story)=>axios.patch(`${"/stories"}/${id}`,story);
export const deleteStory=async(id)=>axios.delete(`${"/stories"}/${id}`);
export const likeStory=async(id)=>axios.patch(`${"s/tories"}/${id}/likeStory`);

export const login=async(formValues)=>api.post("/user/login",formValues);
export const signup=async(formValues)=>api.post("/user/signup",formValues);
